var curHr = today.getHours();
let greeting = ""
const mystyle = {};

if (curHr < 12) {
    greeting = "good morning."
    mystyle.color = "green"
}
else if (curHr < 18) {
    greeting = "good afternoon"
    mystyle.color = "orange"
}
