import React, { useState } from 'react'

const UseReducer1 = () => {

    const [userregister, setUserregister] = useState({
        name: "", email: "", phone: "", work: "", password: ""
    })

    const handleInput = (e) => {
        let name = e.target.name
        let value = e.target.value
        setUserregister({ ...userregister, [name]: value })
    }

    const [allentry, setAllentry] = useState([])
    const submitForm = (e) => {
        e.preventDefault()

        const newEntry = { ...userregister }
        setAllentry([...allentry, newEntry])
        setUserregister({ name: "", email: "", phone: "", work: "", password: "" })
    }

    return (
        <>
            <form onSubmit={submitForm}>

                <div>
                    <label htmlFor="">Name</label>
                    <input type="text" value={userregister.name} onChange={handleInput} name='name' />
                </div>

                <div>
                    <label htmlFor="">Email</label>
                    <input type="text" value={userregister.email} onChange={handleInput} name='email' />
                </div>

                <div>
                    <label htmlFor="">Phone</label>
                    <input type="text" value={userregister.phone} onChange={handleInput} name='phone' />
                </div>

                <div>
                    <label htmlFor="">Work</label>
                    <input type="text" value={userregister.work} onChange={handleInput} name='work' />
                </div>

                <div>
                    <label htmlFor="">password</label>
                    <input type="password" value={userregister.password} onChange={handleInput} name='password' />
                </div>

                <button>submit</button>

            </form>

            {
                allentry.map((curvalue) => {
                    return (
                        <>
                            <p>{curvalue.name}</p>
                            <p>{curvalue.email}</p>
                            <p>{curvalue.phone}</p>
                            <p>{curvalue.work}</p>
                            <p>{curvalue.password}</p>
                        </>
                    )
                })
            }

        </>
    )
}

export default UseReducer1
