import React, { useState } from 'react'

const UseReducer = () => {
    const [Number, setNumber] = useState(0)


    return (
        <>
            <h1>UseReducer </h1>

            <h1>{Number}</h1>
            <button onClick={()=>setNumber(Number+1)}>Inc +</button>
            <button onClick={()=>setNumber(Number-1)}>Inc -</button>
        </>
    )
}

export default UseReducer
