import React, { useState } from 'react'

const ShortCircuit = () => {

    const [name, setname] = useState("")

    let lname = "ckjckj"
    if (lname) {
        <h1>shubham</h1>
    }
    else{
        <h1>chaudhari</h1>
    }

    return (
        <div>
            <h1>{ name || "chuadhari" }</h1>

            <h1>{ lname }</h1>
        </div>
    )
}

export default ShortCircuit
