import React, { useState } from 'react'

const ShortCircuit = () => {

    const [name, setname] = useState("shubham")

    if (name) {
        <h1>shubham</h1>
    }
    else{
        <h1>chaudhari</h1>
    }

    return (
        <div>
            <h1>{ name || "chuadhari" }</h1>
            <h1>{ name }</h1>
        </div>
    )
}

export default ShortCircuit
