import React, { useState } from 'react'

const UseReducer = () => {
    const [Number, setNumber] = useState(0)

    const IncNum = () => {
        setNumber(Number + 1)
    }
    const DecNum = () => {
        if (Number>=1) {
            setNumber(Number - 1)
        }
    }

    return (
        <>
            <h1>UseReducer </h1>

            <h1>{Number}</h1>
            <button onClick={IncNum}>Inc +</button>
            <button onClick={DecNum} >Inc -</button>
        </>
    )
}

export default UseReducer
