import React, { useState } from 'react'
import "./index.css"

const Search = () => {

    const [textinput, setTextinput] = useState("")
    const [imgdisplay, setimgdisplay] = useState("")

    const inputEvent = (event) => {
        setTextinput(event.target.value)
        if (textinput>=3) {
            setimgdisplay(`https://source.unsplash.com/random/600x400?${textinput}`)
        }
    }

   
    // imgdisplay = `https://source.unsplash.com/random/600x400?${textinput}`

    return (
        <>
            <div className="searchbar">

                <input type="text" placeholder="search anything" value={textinput} onChange={inputEvent} />

                <img src={imgdisplay} alt="photo" />

            </div>

        </>
    )
}

export default Search
