import React, { useState } from 'react'

const Todo = () => {
    const photo = "https://source.unsplash.com/random/600x400"

    const [name, setname] = useState('')
    
    const onchangeinput = (event) => {
        setname(event.target.value)
    }
    
    const[img, setImg] = useState("")
    const [show, setshow] = useState()
    const submitform = (e) => {
        e.preventDefault()
        setshow(name)
        setImg(`https://source.unsplash.com/random/600x400?${show}`)
    }

    return (
        <>
            <form action="" onSubmit={submitform}>
                <input type="text" placeholder="enter your name" value={name} onChange={onchangeinput} />
                <img src={show} alt="" />
                <button>submit</button>
                <p>{show}</p>
            </form>
        </>
    )
}

export default Todo
