import React,{useState} from 'react'

const Todos = () => {

    const [inputData, setInputData] = useState("")

    const data =(e)=>{
        setInputData(e.target.value)
    }
    
    const [items, setItems] = useState([])
    const addiItem=()=>{
        setItems([...items ,inputData])
    }

    return (

        <>
            <div className="main-div">
                <div className="child-div">

                    <div className="addItems">
                        <input type="text" placeholder="✍ Add Items..." value={inputData} onChange={data} />
                        <i class="fa fa-plus add-btn" onClick={addiItem} ></i>

                    </div>

                    <div className="showItems">

                        <div className="eachItem" >
                            <h3>{items}</h3>
                            <div className="todo-btn">
                                {/* <i className="far fa-edit add-btn" title="Edit Item"></i> */}
                                <i className="far fa-trash-alt add-btn" title="Delete Item"></i>

                            </div>
                        </div>

                    </div>

                    {/* clear all button  */}
                    <div className="showItems">
                        <button className="btn effect04" data-sm-link-text="Remove All"><span > CHECK LIST </span> </button>
                    </div>

                </div>
            </div>



        </>
    )
}

export default Todos
