                <p>{name} {lname}</p>
                <h1>My current location is {location.pathname}</h1>

                {location.pathname == "/user/shubham/chaudhari" ? (
                    <button onClick={ ()=>{alert("hii")} }>click me</button>
                ) : null}

            </div>
        </>
    )
}

