import React from 'react'

var today = new Date(2020, 5, 27, 15)
var curHr = today.getHours()
let msg = ""
let mystyle={}
if (curHr < 12) {
    msg = "good morning."
    mystyle.color="green"
}
else if (curHr < 18) {
    msg = "good afetrnoon"
    mystyle.color="red"
}
else {
    msg = "good evening"
    mystyle.color ="orange"
}

const App = () => {
    return (
        <div>
            <h1 style={mystyle}>Hello Sir, {msg}</h1>
        </div>
    )
}

export default App
