const initialState = 2

const changeTheNumber = (state = initialState, action) => {
    if (action.type == "INCREMENT") {
        return state + 1
    }
    else if (action.type == "DECREMENT") {
        return state - 1
    }
    return state
}


export default changeTheNumber
