import React, { useState } from 'react'

const Events = () => {

    let purple = "purple"
    let name = "Click Me "
    let [bg, setbg] = useState(purple)

    const myfunction = () => {

        if (bg == purple) {
            setbg("red")
        }
        else {
            setbg("purple")
        }
    }

    return (
        <>
            <div style={{ backgroundColor: bg }}>

                <button onMouseLeave={myfunction}> {name} </button>
            </div>
        </>
    )
}

export default Events
