const Forms = () => {


    const [name, setname] = useState("")
    

    const inputEvent = (event) => {
        console.log(event.target.value)

        setname(event.target.value)
    }
    

    const onSubmit = (event) => {
        event.preventDefault()
        
    }

    return (
        <>
            <div>

                <form >

                    <h1>Hello {fullName} {lnameNew} </h1>
                    <input type="text" placeholder="Enter Your Name" onChange={inputEvent} name="fname" value={name} /><br />
                    <input type="text" placeholder="Enter Your Name" onChange={inputEvent} name="lname" value={lname} /><br />

                    <button type="submit" onClick={onSubmit} >Click Me</button>

                </form>

            </div>
        </>
    )
}

export default Forms
