import React from 'react'

const Clock = () => {

    function clockfunc() {

        let dynamic_time = new Date()

        let t3 = dynamic_time.toLocaleTimeString()
        document.getElementById('clock').innerHTML = t3
    }
    
    let myvar =setInterval(clockfunc, 1000)
  
    function myStopFunction() {
        clearInterval(myVar);
      }


    return (
        <>
            <h1 id="clock">Time</h1>
            <button onClick={myStopFunction}>Click Me</button>
        </>
    )
}

export default Clock
