import React, { useState } from 'react'

const Events = () => {

     let purple = "black"
    let [bg, setbg] = useState(purple)

    const myfunction = () => {
        bg=''
        setbg("red")
        if (bg == purple) {
            setbg("purple")
        }
        else{
            setbg("red")
        }
    }

    return (
        <>
            <div style={{backgroundColor:bg }}>
            
                <button onClick={myfunction}>Click Me</button>
            </div>
        </>
    )
}

export default Events
