import React, { useState } from 'react'

const Forms = () => {


    const [name, setname] = useState("")
    const [fullName, setfullName] = useState("")
    const [lname, setlname] = useState("")
    const [lnameNew, setlnameNew] = useState("")

    const inputEvent = (event) => {
        console.log(event.target.value)

        setname(event.target.value)
    }
    const inputEventTwo = (event) => {
        console.log(event.target.value)

        setlname(event.target.value)
    }

    const onSubmit = (event) => {
        event.preventDefault()
        setfullName(name)
        setlnameNew(lname)
    }

    return (
        <>
            <div>

                <form >

                    <h1>Hello {fullName} {lnameNew} </h1>
                    <input type="text" placeholder="Enter Your Name" onChange={inputEvent} value={name} /><br />
                    <input type="text" placeholder="Enter Your Name" onChange={inputEventTwo} value={lname} /><br />

                    <button type="submit" onClick={onSubmit} >Click Me</button>

                </form>

            </div>
        </>
    )
}

export default Forms
